import React, { Component } from 'react';
import BookType from './bookType'
import PrintType from './printType'

class BookSelector extends Component {
  render() {
    return (  
    <div className ="BookSelector">
        <h1> Book Selector</h1>

         <form
    className="BookSelector_form"
    onSubmit={e => this.props.handleSubmit(e)}
   >
    <label className="searchL" htmlFor="search">
        Search:
    </label>
     <input
            type="text"
            name="search"
            id="search"
            placeholder="book"
            onChange={inp => this.props.handleSearchInput(inp.target.value)}
    />

<input type="submit" value="Submit" />
        </form>

    <label htmlFor="printType">Print Type:</label>
        <PrintType
        //   selectOptions={this.props.selectOptionsPrint}
        //   printChangeHandler={this.props.printChangeHandler}
    />

<label htmlFor="bookType">Book Type:</label>
        <BookType
        //   selectOptions={this.props.selectOptionsBook}
        //   bookChangeHandler={this.props.bookChangeHandler}
        />
</div>
  );
}
}


BookSelector.defaultProps = {
  books: []
};

export default BookSelector;
