import React, { Component } from 'react';
import BookSelector from './bookstoreSelector';
import Book from './book';

class BookApp extends Component {

    constructor(props) {
        super(props);
        this.state = {
          books: [],
          selected: null
        };
      }

componentDidMount() {
        // fetch('https://www.googleapis.com/books/v1/volumes/?q=search+terms/zyTCAlFPjgYC?key=AIzaSyAMGQrqEdaJOMug4ThmQqLhVTqoseQaLUM', {
        //     mode: 'no-cors' // 'cors' by default
        //   })    
        // .then(response => response.json())
        // .then(data => {
        //   const countries = Object.keys(data)
        //         .map(key => data[key].item[0]);
        //   this.setState({
        //     countries
        //   });
        // });
    // }
    const baseUrl = 'https://www.googleapis.com/books/v1/volumes/?q=search+terms/zyTCAlFPjgYC?key=AIzaSyAMGQrqEdaJOMug4ThmQqLhVTqoseQaLUM';
    // const options = {
    //     method: 'GET',
    //     mode: 'no-cors',
    //     headers: {
    //     "Authorization": "AIzaSyAMGQrqEdaJOMug4ThmQqLhVTqoseQaLUM",
    //     "Content-Type": "application/json",
    //     "Access-Control-Allow-Origin": 'http://localhost:3000',
    //     "Access-Control-Allow-Credentials'": 'true',
    let searchEntry = `${this.state.searchEntry}`;
    const queryString = `${baseUrl}?q=${searchEntry}`;

    console.log(queryString);

    fetch(queryString)  
      .then(response => response.json())
      .then(data => {
        const books = Object.keys(data)
              .map(key => data[key].item);
        this.setState({
          books
        });
      });
    }
  setSelected(selected) {
    this.setState({
      selected
    });
  }
 render() {

  const bookstore = this.state.selected
          ? <Book name={this.state.selected['book-names']} books={this.state.selected.name}/>
          : <div className="bookstore_app__placeholder">Select a book</div>;

    return (
      <div className="bookstore_app">
        <BookSelector
          books={this.state.books}
          changeHandler={selected => this.setSelected(selected)}/>
        {bookstore}
      </div>
    );
  }
}
export default BookApp;
