import React, { Component } from 'react';
import './countrySelector.css';

class BookSelector extends Component {
  render() {
    const options = this
          .props
          .books
          .map(
            (book, i) => <option value={book.name} key={i}>{book.name}</option>
          );
    return (
      <div className="book_selector">
        <form>
          <label htmlFor="book">Select a country:</label>
          <select id="book" name="book">
            <option value="None">Select one...</option>
            {options}
          </select>
        </form>
      </div>
    );      
  }
}

BookSelector.defaultProps = {
  countries: []
};

export default BookSelector;
