import React, { Component } from 'react';
// import BookType from './bookType'
// import PrintType from './printType'

class BookSelector extends Component {
    constructor(props) {
        super(props);
        this.initialState = {
            name: '',

        };

        this.state = this.initialState;
    }

    handleChange = event => {
        const { name } = event.target;

        this.setState({
            [name] : ""
        });
    }

    onFormSubmit = (event) => {
        event.preventDefault();

        this.props.handleSubmit(this.state);
        this.setState(this.initialState);
    }

  render() {
    return (     
    <div className ="BookSelector">
        <h1> Book Selector</h1>

         <form
    className="BookSelector_form"
    onSubmit={e => this.props.handleSubmit(e)}
   >
    <label className="searchTerm" htmlFor="search">
        Search:
    </label>
     <input
            type="text"
            name="search"
            id="search"
            placeholder="book"
    />

<input type="submit" value="Submit" />
        </form>

<label htmlFor="print-type">Print Type: </label>
        <select id="print-type" className="print-type" value={this.props.printType}
         onChange={(e) => {this.props.handlePrintFilter(e.target.value)}}>
        </select>     

<label htmlFor="bookType">Book Type:</label>
        <select id="book-type" className="book-type" vlaue={this.props.bookType}
        onChange={(e) => {this.props.handleBookFilter(e.target.vale)}}>
        </select>
   </div>     
  );
}}


BookSelector.defaultProps = {
  books: []
};


export default BookSelector;
