import React, { Component } from 'react';
// import BookType from './bookType'
// import PrintType from './printType'
// import Type from './Type'
// import bookType from './bookType'
// import printType from './printType'

class BookSelector extends Component {
    // constructor(props) {
    //     super(props);
    //     this.initialState = {
