    // }

    // onFormSubmit = (event) => {
    //     event.preventDefault();

    //     this.props.handleSubmit(this.state);
    //     this.setState(this.initialState);
    // }

  render() {
    return (     
    <div className ="BookSelector">
        <h1> Book Selector</h1>

         <form
    className="BookSelector_form"
    onSubmit={e => this.props.handleSubmit(e)}
   >
    <label htmlFor="search">
        Search:
    </label>
     <input
            type="text"
            name="search"
            id="search"
            value={this.props.searchEntry}
            placeholder="book"
            onChange={e => this.props.handleSearch(e.target.value)}/>
    />

<input type="submit" value="Submit" />
        </form>

<label htmlFor="print-type">Print Type: </label>
        <select id="print-type" className="print-type" value={this.props.printType}
         onChange={(e) => {this.props.handlePrintFilter(e.target.value)}}>
            <option value="all">All</option>
          <option value="books">Books</option>
          <option value="magazines">Magazines</option>
        </select>     

<label htmlFor="book-type">Book Type:</label>
        <select id="book-type" className="book-type" vlaue={this.props.bookType}
        onChange={(e) => {this.props.handleBookFilter(e.target.vale)}}>
            <option value="No Filter">No Filter</option>
          <option value="ebooks">Ebooks</option>
          <option value="free-ebooks">Free Ebooks</option>
          <option value="full">Full</option>
          <option value="paid-ebooks">Paid Ebooks</option>
        </select>
   </div>     
  );
}
}


BookSelector.defaultProps = {
  books: []
};



export default BookSelector;
