import React, { Component } from 'react';
import BookSelector from './bookstoreSelector'

class BookApp extends Component {

    constructor(props) {
        super(props);
        this.state = {
          books: [],
          selected: null
        };
      }


componentDidMount() {
    const url = 'https://www.googleapis.com/books/v1/volumes.json?page-size=5000';
    const options = {
        method: 'GET',
        headers: {
        "Authorization": "AIzaSyAMGQrqEdaJOMug4ThmQqLhVTqoseQaLUM",
        "Content-Type": "application/json"
        }
      }; 

    fetch(url,options)  
      .then(response => response.json())
      .then(data => {
        const books = Object.keys(data)
              .map(key => data[key].item[0]);
        this.setState({
          books
        });
      });
  }

  setSelected(selected) {
    this.setState({
      selected
    });
  }
 render() {

  const bookstore = this.state.selected
          ? <bookstore name={this.state.selected['book-names']} books={this.state.selected.name}/>
          : <div className="bookstore_app__placeholder">Select a book</div>;

    return (
      <div className="bookstore_app">
        <BookSelector
          books={this.state.books}
          changeHandler={selected => this.setSelected(selected)}/>
        {bookstore}
      </div>
    );
  }
}
export default BookApp;
